mapper.select:
select DISTINCT lid, l.userid as userid, logtype, function, logcontent, logtime, ip, l.remark,username from l, u, a where l.userid=u.userid and u.aid = a.aid
and a.aid in
'${item}'
and a.aid is null
and UserName LIKE CONCAT(CONCAT('%', #{pd.unamekey}),'%')
and logtype = #{pd.logtype}
and logtime >= #{pd.lastLoginStart}
and logtime <= #{pd.lastLoginEnd}
order by logtime desc
